hudson.tasks.Shell.command:
#!/bin/bash
admin=crowbar$hw_number;
cloud=qa$hw_number;

ssh root@$admin "
export tempestoptions=\"-t\";
  export cloud=$cloud;
  export testsetup=$testsetup;
" '
  hostname -f;
  source mkcloud.config;
  source qa_crowbarsetup.sh;

  if [ "$testsetup" == true ] ; then
     onadmin_testsetup;
  else
     get_novacontroller;
     oncontroller oncontroller_run_tempest;
  fi
'
